import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { userDetails } from '../../../store/user';
import { GET_USER_DETAILS } from '../../../env';
import { ProgressBar } from 'primereact/progressbar';
import { InputText } from 'primereact/inputtext';

type Props = {}

const MyDetails = (props: Props) => {

    const [userData, setUserData] = useState<any>()

    useEffect(() => {
        if (localStorage.getItem("user") != null) {
            const tokenjson = JSON.parse(localStorage.getItem("user") || "");

            axios.post(GET_USER_DETAILS, {
                "token": tokenjson.token
            }).then((res) => {
                setUserData(res.data)
            }).catch((err) => {
                console.log(err);
            })

        }

    }, [])
    console.log(userData);

    return (
        <div>
            {
                userData == undefined ?
                    <ProgressBar mode="indeterminate" style={{ height: '6px' }}></ProgressBar>
                    :
                    <>
                        <InputText value={userData.first_name} style={{ width: '100%' }} />
                        <InputText value={userData.last_name} style={{ width: '100%' }} />
                        <InputText value={userData.email} style={{ width: '100%' }} />
                        <InputText value={userData.phone} style={{ width: '100%' }} />
                        <InputText value={userData.phone2} style={{ width: '100%' }} />
                        <InputText value={userData.street} style={{ width: '100%' }} />
                        <InputText value={userData.city} style={{ width: '100%' }} />
                        <InputText value={userData.country} style={{ width: '100%' }} />
                        <InputText value={userData.balance} style={{ width: '100%' }} />

                        <label htmlFor="username">Username</label>
                        <InputText id="username" aria-describedby="username-help" />
                        <small id="username-help">
                            Enter your username to reset your password.
                        </small>
                    </>

            }

        </div>
    )
}

export default MyDetails
