import React from 'react'
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import multi_categories from '../../data/multi_categories';
import categories from '../../data/categories';


const Container = styled.div`
width: 100%;
/* height: 100vh; */
`;


const MainCat = styled.div`
width: 100%;
display: flex;
flex-wrap: wrap;
height: 300px;
`;
const TitleCat = styled.div`
width: 100%;
display: flex;
height: 80px;
background-color: #019DDA;
/* background-color: #7F5AFF; */
`;
const TitleCatIcon = styled.div<{ background: string }>`
width: 30%;
border-radius: 50%;
aspect-ratio : 1 / 1;

`;



// const MainCat = styled(NavLink)`


// `;

type Props = {}

const Categories = (props: Props) => {
    return (
        <Container>
            {
                //main cat
                categories.map((item: any, index: any) => {

                    //collecting sub cat
                    const items = multi_categories.filter(val => val.id == item.id)

                    //render the main cat and the sub cat under
                    return (
                        <MainCat key={index}>
                            <TitleCat>
                                <TitleCatIcon background={item.pic}>

                                </TitleCatIcon>
                            </TitleCat>
                            {
                                items.map((subcat: any, indexsubcat: any) => (
                                    <div key={indexsubcat}>{subcat.name}</div>
                                ))
                            }
                        </MainCat>
                    )
                })

            }
        </Container>
    )
}

export default Categories
