display: flex;
flex-wrap: wrap;
justify-content: center;
width: 44%;
/* height: 250px; */
margin: 3%;
padding-top: 5px;
background-color: #fff1f0;
user-select: none;
text-decoration: none;
border-radius: 10px;
`;
const Img = styled(motion.img)`
width: 90%;
height: 150px;
background-color: #249e9e;
`;
