import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { userDetails } from '../../../store/user';
import { GET_USER_DETAILS, GET_USER_DETAILS_UPDATE } from '../../../env';
import { ProgressBar } from 'primereact/progressbar';
import { InputText } from 'primereact/inputtext';
import styled from 'styled-components';
import { motion } from 'framer-motion';

type Props = {}


const MyDetails = (props: Props) => {

    const [userData, setUserData] = useState<any>()

    useEffect(() => {
        axios.post(GET_USER_DETAILS, {
            "token": userDetails.value.token
        }).then((res) => {
            setUserData(res.data)
        }).catch((err) => {
            console.log(err);
        })


    }, [userDetails.value.token])

    return (
        <div>
            {
                userData == undefined ?
                    <ProgressBar mode="indeterminate" style={{ height: '6px' }}></ProgressBar>
                    :
                    <UserDetails userData={userData} />
            }
        </div>
    )
}

export default MyDetails

const Title = styled(motion.p)`
width: 100%;
font-size: 20px;
text-align: center;
font-weight: 900;
`;
const ContainerUserDetails = styled(motion.div)`
display: flex;
flex-wrap: wrap;
width: 100%;
padding: 5px;
margin: 0;
margin-top: 10px;
/* background-color: #f5f5f5; */
justify-content: center;
`;
const BoxFiled = styled(motion.div)`
width: 95%;
min-height: 100px;
margin: 10px 0;
&& label {
font-weight: 900;
}
`;

const ContainerUserDetailsNotEdit = styled(motion.div)`
display: flex;
flex-wrap: wrap;
width: 90%;
margin: 5px 20px;
padding: 10px;
background-color: #f3f3f3ca;
justify-content: center;
border-radius: 12px;
`;
const BoxFiledNotEdit = styled(motion.div)`
width: 95%;
min-height: 30px;
display: flex;
flex-wrap: wrap;
align-items: center;
`;
const P = styled(motion.div)`
font-weight: 900;
margin-left:5px ;
`;
const Botton = styled(motion.div)`
width: 90%;
background-color: #7F5AFF;
height: 50px;
