import React, { useEffect } from 'react'
import { NavLink } from 'react-router-dom'
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { userDetails } from '../../store/user';
import { Player } from '@lottiefiles/react-lottie-player';
const ICON = require('./singinAnimation.json');


const Container = styled(motion.div)`
display: flex;
flex-wrap: wrap;
width: 100%;

`;
const Title = styled(motion.div)`
display: flex;
flex-wrap: wrap;
width: 100%;
font-size: 20px;
font-weight: 900;
align-items: center;
justify-content: center;
height: 80px;
`;
const Item = styled(NavLink)`
display: flex;
flex-wrap: wrap;
align-items: center;
justify-content: space-between;
font-size: 18px;
width: 100%;
background-color: #f7f7f7;
padding: 10px;
margin: 5px 15px;
text-decoration: none;
cursor: pointer;
user-select: none;
-webkit-user-select: none; /* Safari */
-ms-user-select: none; /* IE 10 and IE 11 */
user-select: none; /* Standard syntax */
-webkit-tap-highlight-color: transparent;
border-radius: 5px;
height: 80px;
color: #3a3a3a;
`;
const IconAnimation = styled(motion.div)`
padding-top: 50px;
width: 100%;
height: 200px;
`;
type Props = {}

const Account = (props: Props) => {
    const token = userDetails.value.token;

    const exit = () => {
        userDetails.value = { token: false, first_name: 0 };
        localStorage.clear();
    }


    return (
        <Container>
            {
                token ?
                    <>
                        <Title>הפרופיל שלי</Title>
                        <Item to={"lastorders"}>
                            הזמנות אחרונות
                            <span className="material-symbols-rounded">receipt</span>

                        </Item>
                        <Item to={"MyDetails"}>
                            הפרטים שלי
                            <span className="material-symbols-rounded">manage_accounts</span>
                        </Item>
                        <Item to={"lastorders"}>
                            כרטיסי אשראי
                            <span className="material-symbols-rounded">credit_card</span>
                        </Item>
                        <Item to={"/"} onClick={() => exit()}>
                            יציאה
                            <span className="material-symbols-rounded">logout</span>
                        </Item>
                    </>
                    :
                    <>
                        <IconAnimation>
                            <Player
                                autoplay
                                speed={1}
                                loop={true}
                                src={ICON}
                                style={{ height: '150px', width: '200px' }}
                            >
                            </Player>
                        </IconAnimation>
                        <Title>היכנסו או הרשמו לאתר </Title>
                        <Item to={"login"} >
                            כניסה
                            <span className="material-symbols-rounded">login</span>
                        </Item>
                        <Item to={"register"} >
                            הרשמה
                            <span className="material-symbols-rounded">person_add</span>
                        </Item>
                    </>
            }
        </Container>
    )
}

export default Account
