import React from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"


const InStockInside = styled(motion.div)`
/* background-color: #929292; */
width: 80%;
display: flex;
align-items: center;
background-color: #109e03;
height: 100%;
border-radius: 40px;
color:  #ffffff;

`;
const InStock = styled(motion.div)`
display: flex;
width: 45%;
height: 30px;
align-items: center;
/* background-color: #109e03; */
& span{
/* background-color: #929292; */
color:  #ffffff;
margin: 0 2px;
    }

`;




type Props = {
    item: ItemType
}
type ItemType = {
    item_group_id: String,
    item_name: String,
    picture_link: String,
    item_extended_description: String,
    sale_nis: String
}

const ItemDetails = (props: Props) => {
    return (
        <div>

            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">check_circle</span>
                    המוצר במלאי
                </InStockInside>
            </InStock>
            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">package_2</span>
                    12 במארז
                </InStockInside>
            </InStock>
            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">sell</span>
                    3 במבצע
                </InStockInside>
            </InStock>

        </div>
    )
}

export default ItemDetails
