import React from 'react'
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import multi_categories from '../../data/multi_categories';
import categories from '../../data/categories';


const Container = styled.div`
width: 100%;
/* height: 100vh; */
margin-top: 20px;

`;


const MainCat = styled.div`
width: 100%;
display: flex;
flex-wrap: wrap;
height: 300px;

`;
const TitleCat = styled.div`
width: 100%;
display: flex;
height: 80px;
/* background-color: #019DDA; */
/* background-color: #7F5AFF; */
background-color: #ffffff;
-webkit-box-shadow: 0px 8px 23px 9px rgba(0,0,0,0.2); 
box-shadow: 0px 8px 23px 9px rgba(0,0,0,0.2);
align-items: center;
padding: 0 10px;
justify-content: space-between;
`;
const TitleCatText = styled.div`
font-size: 35px;
font-weight: 200;
color: #7F5AFF;
`;
const TitleCatIcon = styled.div<{ background: string }>`
width: 70px;
height: 70px;
aspect-ratio : 1 / 1;
background-color: aqua;
padding: 10px;
border-radius: 50%;
background-image: ${(props) => props.background} ;
background-size: cover;
background-position: center;
`;
const SubCatBox = styled.div`
width: 100%;
height: 100px;
background-color: #ffffff;
/* background-color: #f3f3f3; */
display: flex;
align-items: center;
justify-content: space-between;
overflow-y: hidden; 
overflow-x: scroll; 
 &::-webkit-scrollbar {
    display: none;
 }
`;
const SubCatItem = styled.div<{ background: string }>`
width: 750px;
height: 70px;
border-radius: 50%;
background-image: ${(props) => props.background} ;
background-size: cover;
background-position: center;
`;




// const MainCat = styled(NavLink)`


// `;

type Props = {}

const Categories = (props: Props) => {
    return (
        <Container>
            {
                //main cat
                categories.map((item: any, index: any) => {

                    //collecting sub cat
                    const items = multi_categories.filter(val => val.id == item.id)

                    //render the main cat and the  cat under
                    return (
                        <MainCat key={index}>
                            <TitleCat>
                                <TitleCatText>
                                    {item.name}
                                </TitleCatText>
                                <TitleCatIcon background={`url(${item.pic})`} />
                            </TitleCat>
                            <SubCatBox>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                <div>455645</div>
                                {
                                    items.map((subcat: any, indexsubcat: any) => (
                                        <SubCatItem key={indexsubcat}
                                            background={`url(${subcat.pic})`}
                                        >
                                            {subcat.name}
                                        </SubCatItem>
                                    ))
                                }
                            </SubCatBox>
                        </MainCat>
                    )
                })

            }

        </Container>
    )
}

export default Categories
