import React, { useState } from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"
import { NavLink } from 'react-router-dom';


const Container = styled(NavLink)`
display: flex;
flex-wrap: wrap;
justify-content: center;
width: 44%;
height: 250px;
margin: 3%;
padding-top: 5px;
background-color: #fff1f0;
user-select: none;
text-decoration: none;
border-radius: 10px;
`;
const Img = styled(motion.img)`
width: 90%;
height: 70%;
background-color: #249e9e;
`;
const ItemName = styled(motion.div)`
width: 90%;
height: 10%;
font-size: 15px;
/* font-weight: 600; */
color: #1d1d1d;
`;
const ItemPrice = styled(motion.div)`
width: 90%;
height: 15%;
font-size: 20px;
font-weight: 800;
color: #1d1d1d;
text-align: left;
`;
const Price_first = styled(motion.div)`
font-size: 30px;
font-weight: 800;
`;
const Price_second = styled(motion.div)`
font-size: 20px;
font-weight: 800;
`;


type Props = { item: any }

const Item = (props: Props) => {

    const price = props.item.sale_nis;
    const price_first = price.split(".")[0];
    const price_second = price.split(".")[1];
    return (

        <Container to={`/item/${props.item.item_id}`}
        >
            <Img />
            <ItemName>
                {props.item.item_name}
            </ItemName>
            <ItemPrice>
                <Price_first>
                    {price_first}
                </Price_first>
                <Price_second>
                    {price_second}
                </Price_second>
                â‚ª
            </ItemPrice>
        </Container>

    )
}

export default Item
