import {
    atom,
    selector,
} from 'recoil';
import axios from 'axios';






export const getUserDetails = selector({
    key: 'getUserDetails',
    get: ({ get }) => {

        const getDetails = async () => {
            try {
                const response = await axios.post('https://api.rivhit.co.il/online/RivhitOnlineAPI.svc/Customer.Get', {
                    "api_token": "decd03e5-e35c-41e8-84f7-fba2fb483928",
                    "customer_id": 1
                });
                console.log(response);
            } catch (error) {
                console.error(error);
            }
        }


        return getDetails;
    },
});


export const userDetails = atom({
    key: 'userDetails',
    default: { getUserDetails }
