import React, { useState } from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"
import { NavLink } from 'react-router-dom';


const Container = styled.div`
width: 44%;
height: 250px;

margin: 3%;
background-color: azure;
user-select: none;
`;
const PopUpContainer = styled(motion.div)`
width: 100%;
height: 100%;
background-color: #8f8f8f57;
bottom: 0;
position: fixed;
left: 0;
z-index: 101;
`;
const PopUpItem = styled(motion.div)`
width: 100%;
height: 70%;
background-color: #ffffff;
bottom: 0;
position: fixed;
left: 0;
border-radius: 12px 12px 0 0;
`;
const LinkItem = styled(NavLink)``



type Props = {}

const Item = (props: Props) => {
    const [open, setopen] = useState(false)

    return (
        <Container onClick={() => setopen(state => !state)}>
            Item
            {open && <PopUp />}
        </Container>
    )
}

export default Item


const PopUp = (props: Props) => {

    return (
        <LinkItem to={`/items/1/2`}>

            <PopUpContainer
                initial={{
                    opacity: 0,
                    // y: +1000

                }}
                animate={{
                    opacity: 1,
                    y: 0,
                }}
                transition={{
                    // delay: (0.15 / 30)
                    duration: 0.3,

                }}
            >

                <PopUpItem
                    onClick={(e) => e.stopPropagation()}

                    initial={{
                        opacity: 0,
                        y: +1000

                    }}
                    animate={{
                        opacity: 1,
                        y: 0,
                    }}
                    transition={{
                        // delay: (0.15 / 30)
                        duration: 0.2,

                    }}
                >

                </PopUpItem>
            </PopUpContainer>
        </LinkItem>
    )
}
