import React from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"


const Container = styled(motion.div)`
display: flex;
flex-wrap: wrap;
align-items: center;
justify-content: flex-start;
width: 90%;

`;
const Box = styled(motion.div) <{ background: string }>`
display: flex;
align-items: center;
background-color: ${(props) => props.background} ;
height: 30px;
padding: 0 4px;
& span{
margin: 0 2px;
    }
`;
const InStockInside = styled(motion.div)`
/* background-color: #929292; */
width: 80%;
display: flex;
align-items: center;
background-color: #109e03;
height: 100%;
border-radius: 40px;
color:  #ffffff;

`;
const InStock = styled(motion.div)`
display: flex;
width: 45%;
height: 30px;
align-items: center;
/* background-color: #109e03; */
& span{
/* background-color: #929292; */
color:  #ffffff;
margin: 0 2px;
    }

`;




type Props = {
    item: ItemType
}
type ItemType = {
    item_group_id: String,
    item_name: String,
    picture_link: String,
    item_extended_description: String,
    sale_nis: String
}

const ItemDetails = (props: Props) => {
    return (
        <Container>

            <Box background="#109e03">
                <span className="material-symbols-rounded">check_circle</span>
                <p>המוצר במלאי</p>
            </Box>
            <Box background="#98a111">
                <span className="material-symbols-rounded">package_2</span>
                <p>12 במארז</p>
            </Box>
            <Box background="#df3838">
                <span className="material-symbols-rounded">sell</span>
                <p>3 במבצע</p>
            </Box>




            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">check_circle</span>
                    המוצר במלאי
                </InStockInside>
            </InStock>
            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">package_2</span>
                    12 במארז
                </InStockInside>
            </InStock>
            <InStock>
                <InStockInside>

                    <span className="material-symbols-rounded">sell</span>
                    3 במבצע
                </InStockInside>
            </InStock>

        </Container>
    )
}

export default ItemDetails
