import React from 'react'
import styled from 'styled-components';
import SubCategory from '../SubCategory';
import multi_categories from '../../../data/multi_categories';

const MainCat = styled.div`
width: 100%;
display: flex;
flex-wrap: wrap;
height: 25 0px;

`;
const TitleCat = styled.div`
width: 100%;
display: flex;
height: 80px;
/* background-color: #019DDA; */
/* background-color: #7F5AFF; */
background-color: #ffffff;
-webkit-box-shadow: 0px -18px 20px -6px rgba(0,0,0,0.24); 
box-shadow: 0px -18px 20px -6px rgba(0,0,0,0.24);
border-bottom: 1px solid #f3f3f3;
align-items: center;
padding: 0 10px;
justify-content: space-between;
`;
const TitleCatText = styled.div`
font-size: 35px;
font-weight: 200;
color: #7F5AFF;
`;
const TitleCatIcon = styled.div<{ background: string }>`
width: 70px;
height: 70px;
aspect-ratio : 1 / 1;
background-color: aqua;
padding: 10px;
border-radius: 50%;
background-image: ${(props) => props.background} ;
background-size: cover;
background-position: center;
`;
const SubCatBox = styled.div`
width: 100%;
/* height: 150px; */
/* background-color: #ffffff; */
/* background-color: #f3f3f3; */
display: flex;
flex-wrap: wrap;
align-items: center;
justify-content: space-between;

/* overflow-y: hidden; 
overflow-x: scroll; 
 &::-webkit-scrollbar {
    display: none;
 } */
`;


type Props = {
    index: string,
    item: any
}

const MainCategory = (props: Props) => {
    const items = multi_categories.filter(val => val.id == props.item.id)

    return (
        <MainCat>
            <TitleCat>
                <TitleCatText>
                    {props.item.name}
                </TitleCatText>
                <TitleCatIcon background={`url(${props.item.pic})`} />
            </TitleCat>
            <SubCatBox>
                {
                    items.map((subcat: any, indexsubcat: any) => (
                        <SubCategory item={subcat} index={indexsubcat} key={indexsubcat} />
                    ))
                }
            </SubCatBox>
        </MainCat>)
}

export default MainCategory
