import React, { useState } from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"


const Container = styled.div`
width: 44%;
height: 250px;

margin: 3%;
background-color: azure;
`;
const PopUpContainer = styled(motion.div)`
width: 100%;
height: 100%;
background-color: #8f8f8f57;
bottom: 0;
position: fixed;
left: 0;
z-index: 101;
`;
const PopUpItem = styled(motion.div)`
width: 100%;
height: 50%;
background-color: #ffffff56;
bottom: 0;
position: fixed;
left: 0;
`;



type Props = {}

const Item = (props: Props) => {
    const [open, setopen] = useState(false)

    return (
        <Container onClick={() => setopen(state => !state)}>
            Item
            {open && <PopUp />}
        </Container>
    )
}

export default Item


const PopUp = (props: Props) => {

    return (
        <PopUpContainer
            initial={{
                opacity: 0,
                y: +1000

            }}
            animate={{
                opacity: 1,
                y: 0,
            }}
            transition={{
                // delay: (0.15 / 30)
                duration: 0.2,

            }}
        >

            <PopUpItem>

            </PopUpItem>
        </PopUpContainer>
    )
}
