import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Proccesing from './Proccesing';

const Container = styled(motion.div)`
display: flex;
flex-wrap: wrap;
justify-content: center;
`;
const Img = styled(motion.img)`
background-color: bisque;
width: 90%;
height: 350px;
`;
const Image = styled(motion.img) <{ src: string }>`
background-color: bisque;
width: 90%;
height: 350px;
src: ${(props) => props.src} ;
`;
const ItemName = styled(motion.p)`
font-size: 20px;
`;


type ItemType = {
    item_group_id: String,
    item_name: String,
    picture_link: String,
    item_extended_description: String,
    sale_nis: String
}

const OneItem = () => {
    const [item, setItem] = useState<ItemType | null>(null)
    let { itemid } = useParams();

    useEffect(() => {
        axios.post('http://localhost:3000/web-shop/item', {
            "item_id": itemid
        }).then((res) => {
            setItem(res.data);
            console.log(res.data);
        }).catch((err) => {
            console.log(err);
        })

    }, [])

    return (
        <Container>
            {/* <Img /> */}
            {
                item === null ? <Proccesing /> :
                    // <Item item={item} key={`${index}`} />
                    <>
                        <Image src={"item.item_name"} />
                        <ItemName>{item.item_name}</ItemName>
                    </>


            }
        </Container>
    )
}

export default OneItem

