import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Proccesing from './Proccesing';

const Container = styled(motion.div)`
display: flex;
flex-wrap: wrap;
justify-content: center;
`;
const Img = styled(motion.img)`
background-color: bisque;
width: 90%;
height: 350px;
`;
const Image = styled(motion.img) <{ src: string }>`
background-color: bisque;
width: 90%;
height: 350px;
src: ${(props) => props.src} ;
border-radius: 20px;
`;
const ItemName = styled(motion.p)`
font-size: 20px;
font-weight: 600;
text-align: right;
width: 90%;
`;
const Buttons = styled(motion.div)`
display: flex;
justify-content: space-around;
align-items: center;
align-content:center;
width: 90%;
height: 100px;
`;
const Button = styled(motion.div)`
display: flex;
width: 25%;
justify-content: center;
`;
const ItemPrice = styled(motion.div)`
display: flex;
justify-content: flex-end;
align-items: flex-end;
width: 45%;
height: 30px;
font-size: 20px;
font-weight: 800;
color: #1d1d1d;
text-align: left;

`;
const Price_first = styled(motion.div)`
font-size: 30px;
font-weight: 900;
padding: 0 1px;
margin-bottom: -3px;
font-family: 'Rubik', sans-serif;
`;
const Price_second = styled(motion.div)`
font-size: 20px;
font-weight: 400;
`;
const InStockInside = styled(motion.div)`
/* background-color: #929292; */
width: 80%;
display: flex;
align-items: center;
background-color: #109e03;

`;
const InStock = styled(motion.div)`
display: flex;
width: 45%;
height: 30px;
align-items: center;
/* background-color: #109e03; */
& span{
/* background-color: #929292; */
color:  #cfcfcf;
margin: 0 2px;
    }

`;


type ItemType = {
    item_group_id: String,
    item_name: String,
    picture_link: String,
    item_extended_description: String,
    sale_nis: String
}

const OneItem = () => {
    const [item, setItem] = useState<ItemType | null>(null)
    let { itemid } = useParams();

    const price = item?.sale_nis;
    const price_first = price?.split(".")[0];
    const price_second = price?.split(".")[1];

    useEffect(() => {
        axios.post('http://localhost:3000/web-shop/item', {
            "item_id": itemid
        }).then((res) => {
            setItem(res.data);
            console.log(res.data);
        }).catch((err) => {
            console.log(err);
        })

    }, [])

    return (
        <Container>
            {/* <Img /> */}
            {
                item === null ? <Proccesing /> :
                    // <Item item={item} key={`${index}`} />
                    <>
                        <Image src={"https://wrz.co.il/wp-content/uploads/2021/05/IMG_20210506_234104.jpg"} />
                        <ItemName>{item.item_name}</ItemName>

                        <InStock>
                            <InStockInside>

                                <span className="material-symbols-rounded">check_circle</span>
                                המוצר במלאי
                            </InStockInside>
                        </InStock>
                        <ItemPrice>
                            <Price_second>
                                {price_second}.
                            </Price_second>
                            <Price_first>
                                {price_first}
                            </Price_first>
                            ₪
                        </ItemPrice>
                        <Buttons>
                            <Button>
                                <span className="material-symbols-rounded">add</span>
                            </Button>
                            <Button>
                                <span className="material-symbols-rounded">remove</span>
                            </Button>
                        </Buttons>
                    </>
            }
        </Container>
    )
}

export default OneItem

// item_extended_description
// item_group_id
// item_name
// picture_link
// sale_nis
