import React from 'react'
import styled from 'styled-components';
import { motion } from "framer-motion"
import { NavLink } from 'react-router-dom';


const Container = styled(motion.div)`
display: flex;
flex-wrap: wrap;
align-items: end;
width: 100vw;
bottom: 0;
right: 0;
height: 100vh;
z-index: 101;
background-color: #00000029;
overflow: hidden;
position: fixed;
`;
const Content = styled(motion.div)`
display: flex;
flex-wrap: wrap;
width: 100vw;
bottom: 0;
right: 0;
height: 70%;
background-color: #ffffff;
border-radius:20px 20px 0 0;
`;
const DivImage = styled(motion.div)`
border-radius: 50%;
display: flex;
justify-content: center;
`;
const Image = styled(motion.img) <{ src: string }>`
background-color: bisque;
src: ${(props) => props.src} ;
border-radius: 50%;
object-fit: cover;
width: 90%;
border-radius: 20px;
`;





type Props = {}

const OneItemPopUp = (props: Props) => {
    return (
        <Container>
            <Content
                initial={{
                    y: +1000
                }}
                animate={{
                    y: 0,
                }}
                transition={{
                    duration: 0.2,
                }}
            >
            </Content>
            <DivImage>
                <Image src={"https://wrz.co.il/wp-content/uploads/2021/05/IMG_20210506_234104.jpg"} alt="Avatar" />
            </DivImage>
        </Container>
    )
}

export default OneItemPopUp
